import React from "react";
import styled from "styled-components";

const Box = styled.div`
    width: 400px;
    min-width: 400px;
    height: 240px;
    background-color: #eee;
    margin: 40px;

    border-radius: 7px;
    box-shadow: 2px 2px 4px #999;

    padding: 5px;

    display: flex;
    flex-direction: row;
`;

const PosterDiv = styled.div`
    width: 120px;
    height: 100%;
`;

const ContentDiv = styled.div`
    width: calc(100% - 120px);
    height: 100%;
`;

const PosterImg = styled.img`
    width: 130px;
    object-fit: cover;

    margin-top: -20px;
    margin-left: -20px;

    border-radius: 7px;

    box-shadow: 4px 4px 2px #999;
`;


const MovieBox = () => {
    return (
        <Box>
            <PosterDiv>
                <PosterImg src="https://i.pinimg.com/474x/73/c0/14/73c014968fb1223ff9974519c7bf0393.jpg"></PosterImg>
            </PosterDiv>
            <ContentDiv>ContentDiv</ContentDiv>
        </Box>
    )
};

export default MovieBox;
